import React,{useState} from 'react'
import { Link, useParams } from 'react-router-dom';
import "../adduser/Add.css"


const Edit = () => {
const users={
    fname:"",
    lname:"",
    email:"",    
}

    const {id}=useParams();
    const [user,setUser]=useState(users);

    const inputChangeHandler=(e)=>{
        const {name,value}=e.target;
        setUser({...user,[name]:value})
        
    }

  return (
    <div className='addUser'>
     <Link to={"/"}>Back</Link>
     <h3>Update user</h3>
     <form className='addUserForm'>
        <div className='inputGroup'>
            <label htmlFor='fname'>First Name</label>
            <input type="text" onChange={inputChangeHandler} id="fname" name="fname" autoComplete='off' placeholder='first name'/>
        </div>
        <div className='inputGroup'>
            <label htmlFor='lname'>Last Name</label>
            <input type="text" onChange={inputChangeHandler} id="lname" name="lname" autoComplete='off' placeholder='last name'/>
        </div>
        <div className='inputGroup'>
            <label htmlFor='email'>Email</label>
            <input type="email" onChange={inputChangeHandler} id="email" name="email" autoComplete='off' placeholder='Email'/>
        </div>
        
        <div className='inputGroup'>
            <button type="submit">UPDATE USER</button>
        </div>
     </form>
     </div>
  )
}

export default Edit
